// Put combines Get and Set in a more efficient way where the tree is walked
// only once. The upd(ater) receives (old-value, true) if a KV pair for k
// exists or (zero-value, false) otherwise. It can then return a (new-value,
// true) to create or overwrite the existing value in the KV pair, or
// (whatever, false) if it decides not to create or not to update the value of
// the KV pair.
//
// 	tree.Set(k, v) call conceptually equals calling
//
// 	tree.Put(k, func(generic.U, bool){ return v, true })
//
// modulo the differing return values.
func (t *SecondaryTree) Put(k RouteInfo, upd func(oldV *FinalizerList, exists bool) (newV *FinalizerList, write bool)) (oldV *FinalizerList, written bool) {
	pi := -1
	var p *x2
	q := t.r
	var newV *FinalizerList
	if q == nil {
		// new KV pair in empty tree
		newV, written = upd(newV, false)
		if !written {
			return
		}

		z := t.insert(btDPool2.Get().(*d2), 0, k, newV)
		t.r, t.first, t.last = z, z, z
		return
	}

	for {
		i, ok := t.find(q, k)
		if ok {
			switch x := q.(type) {
			case *x2:
				if x.c > 2*kx2 {
					x, i = t.splitX(p, x, pi, i)
				}
				pi = i + 1
				p = x
				q = x.x[i+1].ch
				continue
			case *d2:
				oldV = x.d[i].v
				newV, written = upd(oldV, true)
				if !written {
					return
				}

				x.d[i].v = newV
			}
			return
		}

		switch x := q.(type) {
		case *x2:
			if x.c > 2*kx2 {
				x, i = t.splitX(p, x, pi, i)
			}
			pi = i
			p = x
			q = x.x[i].ch
		case *d2: // new KV pair
			newV, written = upd(newV, false)
			if !written {
				return
			}

			switch {
			case x.c < 2*kd2:
				t.insert(x, i, k, newV)
			default:
				t.overflow(p, x, pi, i, k, newV)
			}
			return
		}
	}
}

func (t *SecondaryTree) split(p *x2, q *d2, pi, i int, k RouteInfo, v *FinalizerList) {
	atomic.AddUint64(&t.ver, 1)
	r := btDPool2.Get().(*d2)
	if q.n != nil {
		r.n = q.n
		r.n.p = r
	} else {
		t.last = r
	}
	q.n = r
	r.p = q

	copy(r.d[:], q.d[kd2:2*kd2])
	for i := range q.d[kd2:] {
		q.d[kd2+i] = zde2
	}
	q.c = kd2
	r.c = kd2
	var done bool
	if i > kd2 {
		done = true
		t.insert(r, i-kd2, k, v)
	}
	if pi >= 0 {
		p.insert(pi, r.d[0].k, r)
	} else {
		t.r = newX2(q).insert(0, r.d[0].k, r)
	}
	if done {
		return
	}

	t.insert(q, i, k, v)
}

func (t *SecondaryTree) splitX(p *x2, q *x2, pi int, i int) (*x2, int) {
	atomic.AddUint64(&t.ver, 1)
	r := btXPool2.Get().(*x2)
	copy(r.x[:], q.x[kx2+1:])
	q.c = kx2
	r.c = kx2
	if pi >= 0 {
		p.insert(pi, q.x[kx2].k, r)
		q.x[kx2].k = zk2
		for i := range q.x[kx2+1:] {
			q.x[kx2+i+1] = zxe2
		}

		switch {
		case i < kx2:
			return q, i
		case i == kx2:
			return p, pi
		default: // i > kx2
			return r, i - kx2 - 1
		}
	}

	nr := newX2(q).insert(0, q.x[kx2].k, r)
	t.r = nr
	q.x[kx2].k = zk2
	for i := range q.x[kx2+1:] {
		q.x[kx2+i+1] = zxe2
	}

	switch {
	case i < kx2:
		return q, i
	case i == kx2:
		return nr, 0
	default: // i > kx2
		return r, i - kx2 - 1
	}
}

func (t *SecondaryTree) underflow(p *x2, q *d2, pi int) {
	atomic.AddUint64(&t.ver, 1)
	l, r := p.siblings(pi)

	if l != nil && l.c+q.c >= 2*kd2 {
		l.mvR(q, 1)
		p.x[pi-1].k = q.d[0].k
		return
	}

	if r != nil && q.c+r.c >= 2*kd2 {
		q.mvL(r, 1)
		p.x[pi].k = r.d[0].k
		r.d[r.c] = zde2 // GC
		return
	}

	if l != nil {
		t.cat(p, l, q, pi-1)
		return
	}

	t.cat(p, q, r, pi)
}

